<script>

const KEYS = ['a3', 'b3', 'c3', 'd3', 'e3', 'f3', 'g3', 'a4', 'b4', 'c4', 'd4', 'e4', 'g4']
let notes = {}
for (let i = 0; i < KEYS.length; i++) {
  notes[KEYS[i]] = {noteright: false, noteleft: false, sharp: false, flat: false}
}
export default {
  name: 'Notation',
  data () {
    return {
      notes: notes
    }
  },
  computed: {
  },
  props: ['bus'],
  mounted () {
    // this.showChord({c3: 'natural', e3: 'natural', g3: 'natural'})
  },
  created () {
    this.bus.$on('showChord', (args) => {
      this.showChord(args.chord)
    })
  },
  methods: {
    showChord: function (chord) {
      let notes = chord.structure
      let i = null
      for (i in notes) {
        if (this.hasEarlier(i)) {
          this.notes[i].noteright = true
        } else {
          this.notes[i].noteleft = true
        }
        this.notes[i][notes[i]] = true
      }
    },
    hasEarlier: function (note) {
      let i = null
      for (i = 0; i < KEYS.length; i++) {
        if (KEYS[i] === note) return this.notes[KEYS[i - 1]].noteleft
      }
      return false
    }
  }
}
</script>

